export default function reducer(state=[], action ) {
    switch (action.type) {
        case 'ADD_NAME':{
            return {...state,  userDetails:[...state.userDetails ,action.payload ] }
        }
        case 'DELETE':{
            const i = action.payload
            const r ={...state , userDetails:[...state.userDetails.slice(0,i) , ...state.userDetails.slice(i+1 )  ]  }
            
        }
        case 'DELETED_VAL':{
            const o = action.payload
            return{...state , deleted:[...state.userDetails.slice(0,o) , ...state.userDetails.slice(o+1 )  ]  }
        }
        default:{
            return state
        }
    }
}
