export default function reducer(state=[], action ) {
    switch (action.type) {
        case 'ADD_NAME':{
            return {...state,  userDetails:[...state.userDetails ,action.payload ] }
        }
        case "DELETE":{
            const {i}= action
            return{ ...state , userDetails:[...state.userDetails.slice(i)]}
        }
        default:{
            return state
        }
    }
}
