export default function reducer(state=[], action ) {
    switch (action.type) {
        case 'ADD_NAME':{
            return {...state,  userDetails:[...state.userDetails ,action.payload ] }
        }
        case 'DELETE':{
            const {ind} = action.payload
            return{...state , userDetails:[...state.userDetails.slice(0,ind) , ...state.slice(ind+1)]  }
        }
        default:{
            return state
        }
    }
}
