import react,{useState} from 'react'
import Addtodo from './Addtodo'
import { Link } from 'react-router-dom'
import {connect } from 'react-redux'

const mapDispatchToProps = dispatch => {
    return{
        username:(val) => {dispatch({type:'USER', payload:val})}
    }
}

// const mapDispatchToProps = dispatch => {
//     return {
//         addName: (val) => { dispatch({ type: 'ADD_NAME', payload: val }) },
//         deleteName: (val) => { dispatch({ type: 'DELETE', payload: val }) }
//     }
// }
 function Login(props) {
    const [show , setShow ] = useState(false)
    const [name ,setName] = useState('')
    const{username} = props
    
    const subscribeHandeler = e => {
        e.preventDefault();
        setShow(!show);
        {show &&  <Link to='/add'> ko</Link> }
        console.log(show)
    };
    
    return (
        <div>
                <h2>Login here  </h2>
                { !show &&(
                    <form onSubmit ={subscribeHandeler}>
                    <input type="text" placeholder='Name' onChange={(e) => {setName(e.target.value)}}/> <br /> <br />
                    <br />{name}
                    <input type="password" placeholder='Password' /> <br /> <br />
                    <button type ='submit' style={{margin:'5px'}} > Submit </button>
                </form> 
                )}
        </div>
    )
                
}

export default connect(null , mapDispatchToProps)(Login)
