import React from 'react'
import { connect } from 'react-redux'
import {useState , useEffect } from 'react'


const mapDispatchToProps = dispatch => {
    return{
        userinputadd : (val) => { dispatch ({ type:'Add' , payload:val})}
    }
}

function Addtodo() {
    return (
        <div >
            <h2>Add an item </h2>
            
            <input type="text" onC /> 
           <br /> <button  style={{border:'2px solid black' , margin:'5px'}} > Add ToDo</button>

        </div>
    )
}
export default connect(null , mapDispatchToProps)(Addtodo)
