import Header from './todolist/header'
import Addtodo from './todolist/Addtodo'
import Todolist from './todolist/Todolist'
import Reducer from './todolist/mainReducer'
import Login from './todolist/Login'

import React from 'react'
import { createStore } from 'redux'
import { Provider } from 'react-redux'
import { BrowserRouter, Switch, Link, Route } from 'react-router-dom'

const state = {
  userinput: ['tushar malhan is the best', 'u bitch `'],
  userout: []
}

const store = createStore( Reducer , state)

const sty ={
  textAlign: 'center', margin: "70px" ,
}

export default class App extends React.Component {
  constructor(props){
    super(props)
}
  render() {
    return (
      <div>


      <Provider store={store}>
          <BrowserRouter>
        <div style={sty}>
          <Header />
          <Switch>
          <Route exact path='/' component={Login} />
            <Route path='/add' component={Addtodo} />
            <Route path='/comments' component={Todolist} />
          <Todolist />
          </Switch>
        </div>
        </BrowserRouter>
      </Provider>
            </div>
    )
  }
}
