export default function reducer(state=[], action ) {
    switch (action.type) {
        case 'ADD_NAME':{
            return {...state,  userDetails:[...state.userDetails ,action.payload ] }
        }
        case 'DELETE':{
            const i = action.payload
            return{...state , deleted :{ userDetails: [...state.userDetails.slice(0,i) , ...state.userDetails.slice(i+1 )  ] } }
        }
        default:{
            return state
        }
    }
}
