import react,{useState} from 'react'
import Addtodo from './Addtodo'
import { Link } from 'react-router-dom'
import {connect } from 'react-redux'

const mapDispatchToProps = dispatch => {
    return{
        username:(val) => {dispatch({type:'USER', payload:val})}
    }
}

 function Login(props) {
    const [show , setShow ] = useState(false)
    const [name ,setName] = useState('')
    const{username} = props
    
    // const subscribeHandeler = e => {
    //     e.preventDefault();
    //     setShow(!show);
    //     {show &&  <Link to='/add'> ko</Link> }
    //     console.log(show)
    // };
    console.log(!show)
    return (
        <div>
                <h2>Login here  </h2>
                { !show &&(
                    <div>
                    <input type="text" placeholder='Name' onChange={(e) => {setName(e.target.value)}} style={{ border: '2px solid black', margin: '5px' }}/> <br /> <br />
                    <input type="password" placeholder='Password' style={{ border: '2px solid black', margin: '5px' }} /> <br /> <br />
                    <button type ='submit' style={{ border: '2px solid black', margin: '5px' }} onClick={() => {username(name)}}> Submit </button>
                    </div>
                )}
        </div>
    )
                
}

export default connect(null , mapDispatchToProps)(Login)
