export default function reducer(state=[], action ) {
    switch (action.type) {
        case 'ADD_NAME':{
            return {...state,  userDetails:[...state.userDetails ,action.payload ] }
        }
        case 'DELETE':{
            const {ind , name } = action.pay
            return{...state , userDetails:[...state.userDetails.pop(ind)]  }
        }
        default:{
            return state
        }
    }
}
